(function(){

    var app = angular.module('list', [ ]);

    var items = [
        'milk',
        'bread',
        'eggs'
    ];

    app.controller('ListController', function(){
        this.items = items;
        this.counter = items.length;

    });

    app.controller('FormController', function(){
        this.string = [];
        console.log(this.string);
        console.log(items);
        this.addGrocery = function(grocery) {
            console.log(grocery);
            console.log(items);
            if (grocery == '') {
                alert("Please Enter An Item.");
                return;
            }
            else {
                var sorted_arr = items.sort();
                for (var i = 0; i < items.length; i++) {
                    if (grocery == sorted_arr[i]) {
                        alert("duplicate");
                        return;
                    }
                }
                items.push(grocery);
                this.string = [];
            }
        };
    });

    app.controller('SubController', function(){
        this.subGrocery = function(grocery) {
            var index = items.indexOf(grocery);
            items.splice(index,1);
        }

    });


})();