

    var app = angular.module('EmployeeDirectory', []);


    app.controller('ListController', function($scope, dataService){

        this.directory = dataService.getDirectory();
        console.log(this.directory)

    });

    app.controller('FormController', function($scope, dataService){
        this.hireEmployee = function(){

            dataService.hireEmployee(this.name,this.street,this.city,this.state,this.zip);

        };


        this.termEmployee = function(){

            dataService.termEmployee($scope.Object);

        }

    });

