

    var app = angular.module('EmployeeDirectory', ['ngRoute']).config(function($routeProvider){

        $routeProvider.when('/view1',{

            templateUrl : "view1.html",
            controller : "View1Controller",
            controllerAs: "formCtrl"

        }).when("/view2",{
            templateUrl : "view2.html",
            controller : "View2Controller",
            controllerAs: "expenseCtrl"

        }).when("/view3/:paramTest",{
            templateUrl : "view3.html",
            controller : "View3Controller",
            controllerAs : "expenseDetailCtrl"

        }).otherwise({
            redirectTo : "/view1"

        });

    });


    app.controller("View1Controller", function($scope, dataService){


        this.directory = dataService.getDirectory();
        console.log(this.directory);

        this.hireEmployee = function(){

            dataService.hireEmployee(this.name,this.street,this.city,this.state,this.zip);

            this.name = null;
            this.street = null;
            this.city = null;
            this.state = null;
            this.zip = null;

        };


        this.termEmployee = function(){

            console.log($scope.Object);
            dataService.termEmployee($scope.Object);


        }


    });

    app.controller("View2Controller", function($scope, dataService){

        this.expenser = dataService.getExpenses();
        console.log(this.expenser);

        this.submitExpense = function(){

            dataService.submitExpense(this.description, this.amount);

            this.description = null;
            this.amount = null;

        };

        this.cancelExpense = function(){

            console.log($scope.Object);
            dataService.cancelExpense($scope.Object);

        }

    });

    app.controller("View3Controller", function($scope, $routeParams, dataService){

        $scope.otherTest = $routeParams.paramTest;
        console.log($scope.otherTest);

        this.expense = dataService.getExpenses();
        console.log(this.expense);
        this.detail = this.expense[$scope.otherTest];
        console.log(this.detail);

        this.updateExpense = function(){

            dataService.updateExpense(this.expenseType, this.expenseCleared, this.expenseDate, $scope.otherTest);

        }

    });

